mapper.update:
update UserSetting


        email = #{email},


        follow_type = #{followType},


        last_update_time = #{lastUpdateTime},


        extend_json = #{extendJson}


where uid = #{uid}

mapper.select:
select email, follow_type, extend_json, userrole
from UserSetting
where uid = #{uid} and userrole = #{role}

select count(*)
from UserSetting
where username = #{username} and userpwd = #{pwd}

    and userrole = #{role}

select uid, username, email
from UserSetting
where userrole = #{0}
